import fetch from 'isomorphic-fetch';
import { ThemeProvider } from 'styled-components';
import theme from '../styles/themes/Default/index';
import GlobalStyle from '../styles/GlobalStyles';
import NewsFeedsList from '../components/organisms/NewsFeeds/NewsFeedsList';
import Header from '../components/molecules/Header/Header';
import Footer from '../components/molecules/Footer/Footer';

import CommentList from '../components/organisms/CommentsList/CommentsList';
export default class NewsFeed extends React.Component{

    static async getInitialProps ({req, res, query}) {

        let newsFeed;

        try{
            const newsId = query.id;
            const response = await fetch(
                `https://node-hnapi.herokuapp.com/item/${newsId}`
              );

              newsFeed = await response.json();

        }catch(error){
            console.log(error);
            newsFeed = null;

        }
        return {newsFeed};
    }

    render(){
        const { newsFeed } = this.props;
        return (
            <ThemeProvider theme={this.theme}>
                <GlobalStyle />
                <Header title="Hacker News"  description="A Hacker News clone made with Next.js"></Header>
                <h1 className="news-title">
            <a href={newsFeed.url}>{newsFeed.title}</a>
          </h1>
          <div className="news-details">
            <strong>{newsFeed.points} points</strong>
            <strong>{newsFeed.comments_count} comments</strong>
            <strong>{newsFeed.time_ago}</strong>
          </div>
                <main>
                {newsFeed.comments.length > 0 ? (
                 <CommentList comments={newsFeed.comments} />
              ) : (
                <div>No comments for this story</div>
              )}

            </main>


                <Footer page={page}></Footer>
                <style jsx>{`
          main {
            padding: 1em;
          }
          .news-title {
            font-size: 1.2rem;
            margin: 0;
            font-weight: 300;
            padding-bottom: 0.5em;
          }
          .news-title a {
            color: #333;
            text-decoration: none;
          }
          .news-title a:hover {
            text-decoration: underline;
          }
          .news-details {
            font-size: 0.8rem;
            padding-bottom: 1em;
            border-bottom: 1px solid rgba(0, 0, 0, 0.1);
            margin-bottom: 1em;
          }
          .news-details strong {
            margin-right: 1em;
          }
          .news-details a {
            color: #f60;
          }
        `}</style>
                </ThemeProvider>




        )
    }

}
